import React, { useState } from 'react';

const Member_login = () => {
  const [loginFormData, setLoginFormData] = useState({
    loginUsername: '',
    loginPassword: ''
  });

  const [signupFormData, setSignupFormData] = useState({
    signupUsername: '',
    signupEmail: '',
    signupLocation: '',
    signupPassword: '',
    signupConfirmPassword: ''
  });

  const handleLoginSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/loginuser', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(loginFormData)
      });

      if (response.ok) {
        console.log('User logged in successfully');
      } else {
        console.error('Error logging in:', response.statusText);
      }
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  const handleSignupSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/createuser', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(signupFormData)
      });

      if (response.ok) {
        console.log('User signed up successfully');
      } else {
        console.error('Error signing up user:', response.statusText);
      }
    } catch (error) {
      console.error('Error signing up user:', error);
    }
  };

  const handleLoginChange = (event) => {
    const { name, value } = event.target;
    setLoginFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value
    }));
  };

  const handleSignupChange = (event) => {
    const { name, value } = event.target;
    setSignupFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value
    }));
  };

  return (
    <div>
      {/* Login Form */}
      <div id="member" className="tabcontent">
        <form onSubmit={handleLoginSubmit}>
          {/* Login form fields */}
          <input type="text" name="loginUsername" value={loginFormData.loginUsername} onChange={handleLoginChange} required />
          <input type="password" name="loginPassword" value={loginFormData.loginPassword} onChange={handleLoginChange} required />
          <input type="submit" value="Log in" />
          <button class="tablinks green-button" onclick="openTab(event, 'forgot')">Forgot Password</button>
			<h3> </h3>
			<label> Don't have an account?</label>
			<button class="tablinks green-button" onclick="openTab(event, 'signup_member')"> Join as member </button>
		
        </form>
     
      {/* Signup Form */}
      <div id="signup_member" className="tabcontent">
        <form onSubmit={handleSignupSubmit}>
          {/* Signup form fields */}
          <input type="text" name="signupUsername" value={signupFormData.signupUsername} onChange={handleSignupChange} required />
          <input type="email" name="signupEmail" value={signupFormData.signupEmail} onChange={handleSignupChange} required />
          <input type="text" name="signupLocation" value={signupFormData.signupLocation} onChange={handleSignupChange} required />
          <input type="password" name="signupPassword" value={signupFormData.signupPassword} onChange={handleSignupChange} required />
          <input type="password" name="signupConfirmPassword" value={signupFormData.signupConfirmPassword} onChange={handleSignupChange} required />
          <input type="submit" value="Sign Up" />
        </form>
      </div>
    </div>
    </div>
  );
};

export default Member_login;
