import React, { useState } from 'react';

export default function Statistics() {
    const pointsData = [
        {
          year: 2022,
          teams: [
            { name: 'Team A', points: 10 },
            { name: 'Team B', points: 8 },
            { name: 'Team C', points: 6 },
          ],
        },
        {
          year: 2021,
          teams: [
            { name: 'Team X', points: 12 },
            { name: 'Team Y', points: 9 },
            { name: 'Team Z', points: 7 },
          ],
        },
        // Add more years and teams as needed
    ];
    
    const [selectedYear, setSelectedYear] = useState(pointsData[0].year);
    
    // Filter points based on the selected year
    const filteredPoints = pointsData.find(data => data.year === selectedYear);
      
    return (
        <div>
            <section id="statistics" className="points-section">
                <div className="container">
                    <h2 className="section-heading">Points Table</h2>
                    {/* Dropdown menu for selecting year */}
                    <div className="dropdown">
                        <button className="dropdown-button" onClick={(e) => e.currentTarget.nextSibling.classList.toggle("show")}>
                            Select Year
                        </button>
                        <div className="dropdown-content">
                            {/* Map through pointsData to create dropdown options */}
                            {pointsData.map((data, index) => (
                                <button key={index} className="dropdown-item" onClick={() => setSelectedYear(data.year)}>
                                    {data.year}
                                </button>
                            ))}
                        </div>
                    </div>
                    {/* Display points table for the selected year */}
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th>Team</th>
                                <th>Points</th>
                            </tr>
                        </thead>
                        <tbody>
                            {/* Map through filteredPoints.teams to render each team */}
                            {filteredPoints && filteredPoints.teams.map((team, index) => (
                                <tr key={index}>
                                    <td>{team.name}</td>
                                    <td>{team.points}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </section>
        </div>
    );
}
