import React,{useState} from 'react';
import './Member_signup.css';
import Member_login from './Member_login';
export default function Member_signup() {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        location: '',
        password: '',
        confirm_password: ''
      });
    
    
      const handleSubmit = async (event) => {
        event.preventDefault();
    
        try {
          const response = await fetch('http://localhost:5000/api/createuser', {
            method: 'POST', 
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(formData)
          });
    
          if (response.ok) {
            // Handle successful signup
            console.log('User signed up successfully');
          } else {
            // Handle signup error
            console.error('Error signing up user');
          }
        } catch (error) {
          console.error('Error signing up user', error);
        }
      };
    
      const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevFormData) => ({
          ...prevFormData,
          [name]: value
        }));
      };
    
    



  return (
    <div>
        <Member_login/>
      <div id="signup_member" className="tabcontent">
        <form onSubmit={handleSubmit}>
          <label htmlFor="username">Username</label>
          <input type="text" name="username" value={formData.username} onChange={handleChange} required />
          
          <label htmlFor="email">Email</label>
          <input type="text" name="email" value={formData.email} onChange={handleChange} required />
          
          <label htmlFor="location">Location</label>
          <input type="text" name="location" value={formData.location} onChange={handleChange} required />
          
          <label htmlFor="password">Password</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange} required />
          
          <label htmlFor="confirm_password">Confirm Password</label>
          <input type="password" name="confirm_password" value={formData.confirm_password} onChange={handleChange} required />
          
          <input type="submit" value="Sign up" />
        </form>
      </div>

      <div id="forgot_member" className="tabcontent">
        <form method="post" action="/reset_password">
          <label htmlFor="username">Username</label>
          <input type="text" name="username" required />
          <label htmlFor="password">New Password</label>
          <input type="password" name="password" required />
          <input type="submit" value="Reset Password" />
        </form>
      </div>


    </div>
  )
}const openTab = (tabName) => (evt) => {
    // Declare all variables
    var i, tabcontent, tablinks;

    // Get all elements with class="tabcontent" and hide them
    tabcontent = document.getElementsByClassName("tabcontent");
    for (i = 0; i < tabcontent.length; i++) {
      tabcontent[i].style.display = "none";
    }

    // Get all elements with class="tablinks" and remove the class "active"
    tablinks = document.getElementsByClassName("tablinks");
    for (i = 0; i < tablinks.length; i++) {
      tablinks[i].className = tablinks[i].className.replace(" active", "");
    }

    // Show the current tab, and add an "active" class to the button that opened the tab
    document.getElementById(tabName).style.display = "block";
    evt.currentTarget.className += " active";
  };
