// Signin.js

import React from 'react';
import  './Signin.css'; // Import CSS Module

export default function Signin() {
  // Function to switch between tabs
  function openTab(tabName) {
    return function (evt) {
      // Declare all variables
      var i, tabcontent, tablinks;

      // Get all elements with class="tabcontent" and hide them
      tabcontent = document.getElementsByClassName(styles.tabcontent); // Use styles.tabcontent
      for (i = 0; i < tabcontent.length; i++) {
        tabcontent[i].style.display = "none";
      }

      // Get all elements with class="tablinks" and remove the class "active"
      tablinks = document.getElementsByClassName(styles.tablinks); // Use styles.tablinks
      for (i = 0; i < tablinks.length; i++) {
        tablinks[i].className = tablinks[i].className.replace(" active", "");
      }

      // Show the current tab, and add an "active" class to the button that opened the tab
      document.getElementById(tabName).style.display = "block";
      evt.currentTarget.className += " active";
    };
  }

  return (
    <div>
      <h1>Waste Management System</h1>

      <div className={styles.tab}>
        <button className={styles.tablinks} onClick={openTab('admin')}>Admin</button>
        <button className={styles.tablinks} onClick={openTab('player')}>Player</button>
        <button className={styles.tablinks} onClick={openTab('user')}>User</button>
      </div>

      <div id="admin" className={styles.tabcontent}>
        <form method="post" action="/admin_login_view">
          <label htmlFor="username">Username</label>
          <input type="text" name="username" required />
          <label htmlFor="password">Password</label>
          <input type="password" name="password" required />
          <input type="submit" value="Login" />
        </form>
      </div>

      <div id="player" className={styles.tabcontent}>
        {/* Player tab content */}
      </div>

      <div id="user" className={styles.tabcontent}>
        {/* User tab content */}
      </div>

      {/* Other tab content */}

    </div>
  );
}
